import pypsa

network = pypsa.Network()

# add slack bus/generator
network.add("Bus", "Slack bus")
network.add("Generator", "Slack generator", bus="Slack bus", control="Slack")

# add second bus with load and generator
network.add("Bus","Bus 1")
network.add("Generator", "Generator 1", bus="Bus 1", p_set=1000)
network.add("Load", "Load 1", bus="Bus 1", p_set=100)

# connect buses by a line
network.add("Line", "Line slack-1", x=0.01, r=0.01, bus0="Slack bus", bus1="Bus 1")

network.pf()